import React from 'react';
import Footer from '../Components/Footer';
import Header from '../Components/Header';
import teachers from '../data/dataTeacher';
import SocialMidea from '../Components/SocialMedias';
import { Link } from 'react-router-dom';
import joseRoberto from '../images/joseRoberto.jpeg'

export default function AboutMe() {
  return(
    <>
      <Header />
      <main>
        <section>
          <h1>Evolution Sport Center</h1>
            <p> A academia foi fundada em 2014 pelo professor Jose Roberto e amigos que  acreditaram no seu potencial para administrar e transforma-la em um ambiente agradável para treinar.</p>
            <p>Mas foi passando por vários processos de mudança como: local, estrutura, nome e logo que a deixou de cara nova.</p>
            <p>O Centro de Treinamento tem inúmeros alunos que buscavam as aulas de lutas sem o objetivo competitivo, apenas para controle e tratamento de diversas patologias.</p>
        </section>
        <section>
          <h3>{ teachers[0].name }</h3>
          <img 
            alt={`Foto do professor(a) ${  teachers[0].name }`}
            src={ teachers[0].photo }
          />
          <span><i>{ teachers[0].inspiration}</i></span>
          <SocialMidea
            thisMidea={ ( teachers[0].facebook !== null) ? true : false }
            urlFaceBook={  teachers[0].facebook }
            urlInstagran={  teachers[0].instagran }
          />
          <Link to={`/quem-somos/${ teachers[0].id}`} >
            <button
            type="button"
            >
              Mais Detalhes
            </button>
            </Link>

          </section>
     </main>
    <Footer
    urlInstagran="https://www.instagram.com/evolutionsportcenter/"
    />
    </>
  );
}
